import classes from "./Quiz.module.css";
import { quizData,hindidata } from "../Data/QuizData";
import { useParams,useNavigate } from "react-router-dom";
import { useSelector } from "react-redux";
import { useState } from "react";


const Quiz = () => {
  const[clickOption,setClickOption]=useState(0)
  const[score,setScore]=useState(0)
  const languageselect = useSelector((state) => state.language.value)
  const language = languageselect ? quizData : hindidata
 let {quizID} = useParams()
 let navigate = useNavigate()


const nextQuestion = () => {
  if(quizID <language.length){
  navigate(`/quiz/${Number(quizID)+1}`)
  }
};


const previousQuestion = () => {
  if(quizID > language.length-4){
  navigate(`/quiz/${Number(quizID)-1}`)
  }
};

  return (
    <div className={classes.maincontainer}>
      <div>
        <p className={classes.heading}>Quiz APP</p>
        <div className={classes.container}>
          <div className={classes.question}>
            <span className={classes.questionnumber}>
              {quizID}.
            </span>
            <span>{language[quizID-1].question}</span>
          </div>
          <div className={classes.option}>
            {language[quizID-1].options.map((option,i) => {
              return <button className={classes.optionbtn} onClick={()=>{setClickOption(i+1)}}>{option}</button>;
            })}
          </div>
          <input
            type="button"
            value="Next"
            className={classes.button}
            onClick={nextQuestion}
          />
          <input
            type="button"
            value="Previous"
            className={classes.button}
            onClick={previousQuestion}
          />
        </div>
      </div>
    </div>
  );
};
export default Quiz;
