import "./Quiz.css";
import { quizData, hindidata } from "../Data/QuizData";
import { useParams, useNavigate, NavLink } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { useState } from "react";
import { AnswerShow } from "./AnswerSlice";
import { finalanswershow } from "./FinalAnswerSlice";

const Quiz = () => {
  const [clickOption, setClickOption] = useState(0);
  const dispatch = useDispatch();
  const languageselect = useSelector((state) => state.language.value);
  const language = languageselect ? quizData : hindidata;
  let { quizID } = useParams();
  let navigate = useNavigate();
  const finalanswer = useSelector((state) => {
    return state.finalanswer.finalanswer;
  });
  let yourans = Object.values(finalanswer);
  console.log(yourans);

  // useEffect(() => {
  //   console.log(score);
  //   dispatch(UpdateScore(score));
  // }, [score]);

  const firstButton = ()=> {
    navigate('/quiz/1')
  }

  const nextQuestion = () => {
    if (quizID < language.length) {
      navigate(`/quiz/${Number(quizID) + 1}`);
    }
    // setClickOption(0);
    dispatch(finalanswershow({ i: quizID, value: clickOption }));
  };

  const previousQuestion = (i) => {
    if (quizID > language.length - 4) {
      navigate(`/quiz/${Number(quizID) - 1}`);
    }
  };

  //console.log(clickOption,{i:quizID,value:clickOption})
  return (
    <div className="maincontainer">
      <div>
        <p className="heading">Quiz APP</p>
        <div className="container">
          <div className="question">
            <span className="questionnumber">{quizID}.</span>
            <span>{language[quizID - 1].question}</span>
          </div>
          <div className="option">
            {language[quizID - 1].options.map((option, i) => {
              return (
                <button
                  key={i}
                  className={`optionbtn ${
                    clickOption === option ? "checked" : null
                  }`}
                  onClick={() => {
                    setClickOption(option, i);
                    console.log(clickOption);
                  }}
                >
                  {option}
                </button>
              );
            })}
          </div>
          {Number(quizID) !== language.length && (
            <input
              type="button"
              value="Next"
              className="button"
              onClick={nextQuestion}
            />
          )}
          {Number(quizID) === language.length && (
            <input
              type="button"
              value="Submit"
              className="button"
              onClick={() => {
                dispatch(AnswerShow(language));
                navigate("/result");
                dispatch(finalanswershow({ i: quizID, value: clickOption }));
              }}
            />
          )}

          {Number(quizID) !== language.length - 4 && (
            <input
              type="button"
              value="Previous"
              className="button"
              onClick={previousQuestion}
            />
          )}
          <div className="direct">
            <button onClick={firstButton}>
              <NavLink className="nav" to="/1">1</NavLink>
            </button>
            <button>
              <NavLink className="nav" to="/2">2</NavLink>
            </button>
            <button>
              <NavLink className="nav" to="/3">3</NavLink>
            </button>
            <button>
              <NavLink className="nav" to="/4">4</NavLink>
            </button>
            <button>
              <NavLink className="nav" to="/5">5</NavLink>
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};
export default Quiz;
