import "./Quiz.css"
import { quizData,hindidata } from "../Data/QuizData";
import { useParams,useNavigate } from "react-router-dom";
import { useSelector,useDispatch } from "react-redux";
import { useState,useEffect } from "react";
import { AnswerShow } from "./AnswerSlice";
import { finalanswershow } from "./FinalAnswerSlice";
import { UpdateScore } from "./ScoreSlice";

const Quiz = () => {
  const[clickOption,setClickOption]=useState(0)
  // const[score,setScore]=useState(0)
  const dispatch = useDispatch()
  const languageselect = useSelector((state) => state.language.value)
  const language = languageselect ? quizData : hindidata
 let {quizID} = useParams()
 let navigate = useNavigate()

 useEffect(()=>{console.log(score)},[score])

const nextQuestion = () => {
  dispatch(UpdateScore(score+1))

  if(quizID <language.length){
  navigate(`/quiz/${Number(quizID)+1}`)
  }
  setClickOption(0)
  dispatch(finalanswershow({i:quizID, value:clickOption}))

};


const previousQuestion = (i) => {
  if(quizID > language.length-4){
  navigate(`/quiz/${Number(quizID)-1}`)
  }
  setClickOption()
};

/*const updateScore =()=>{
  if(clickOption === language[quizID-1].answer){
    setScore(score+1);
  }
 }*/
  //console.log(clickOption,{i:quizID,value:clickOption})
  return (
    <div className="maincontainer">
      <div>
        <p className="heading">Quiz APP</p>
        <div className="container">
          <div className="question">
            <span className="questionnumber">
              {quizID}.
            </span>
            <span>{language[quizID-1].question}</span>
          </div>
          <div className="option">
            {language[quizID-1].options.map((option,i) => {
              return <button key={i} className={`optionbtn ${clickOption === option ? "checked":null}`} onClick={()=>{setClickOption(option)}}>{option}</button>;
            })}
          </div>
          {Number(quizID)!== language.length && <input
            type="button"
            value="Next"
            className="button"
            onClick={nextQuestion}
          />}
          {Number(quizID)=== language.length && <input
            type="button"
            value="Submit"
            className="button"
            onClick={()=>{
            updateScore();
            dispatch(AnswerShow(language))
            navigate("/result")
            dispatch(finalanswershow({i:quizID, value:clickOption}))

            }}
            />}

          {Number(quizID)!== language.length-4 && <input
            type="button"
            value="Previous"
            className="button"
            onClick={previousQuestion}
          />}
        </div>
      </div>
    </div>
  );
};
export default Quiz;
