      {visible && <div className="maincontent">
        {language.map((item, i) => {
          return (
            <div>
              <section className="mainsection">
                <div>
                  <span>{i + 1}</span>
                  <span>{item.question}</span>
                </div>
                <div className="showoption">
                  {item.options.map((option) => {
                    return <button value={option} className={report?'green':'red'}>{option}</button>;
                  })}
                </div>
                <div>
                  <p id="correct">CORRECT ANSWER: {item.answer}</p>
                  <p id="ownans">YOUR ANSWER: {yourans[i]}</p>
                </div>
              </section>
            </div>
          );
        })}
      </div>}
