import "./FormData.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { NameShow, SurnameShow } from "./InfoSlice";
import { useDispatch } from "react-redux";

const FormData = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");
  const [nameError,setNameError] = useState(false)
  const [surnameError,setSurnameError] = useState(false)

  const nameHandler = (event) => {
    setName(event.target.value);
    setSurnameError(false)
    // console.log(event.target.value)
    dispatch(NameShow(event.target.value));
  };

  const surnameHandler = (event) => {
    setSurname(event.target.value);
    setNameError(false)
    //console.log(event.target.value)
    dispatch(SurnameShow(event.target.value));
  };

  const nevigate = useNavigate();

  const switchLanguagepage = (e) => {
    e.preventDefault();
    if (name.trim() === "") {
      setNameError(true)
    } else if (surname.trim() === "") {
      setSurnameError(true)
    }
    nevigate("/language");
  };
  return (
    <div className="maininfo">
      <form className="information">
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          value={name}
          className="inputcontent"
          placeholder="name"
          onChange={nameHandler}
        />
        {setNameError && (<span>please Fill name details</span>)}
        <label htmlFor="surname">Surname:</label>
        <input
          type="text"
          value={surname}
          className="inputcontent"
          placeholder="surname"
          onChange={surnameHandler}
        />
        {setSurnameError && (<span>please Fill surname details</span>)}
        <div className="radiocontent">
          <label htmlFor="Gender" id="gender">
            Gender:
          </label>
          <input type="radio" name="Gender" value="Male" id="male" />
          Male
          <input type="radio" name="Gender" value="Female" id="female" />
          Female
        </div>
        <button onClick={switchLanguagepage}>Next</button>
      </form>

    </div>
  );
};
export default FormData;
