import "./FormData.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
const FormData = () => {
  const[name,setName]=useState("")
  const[surname, setSurname]=useState("")

  const nameHandler = (event)=>{
      setName(event.target.value)
  }
  const surnameHandler = (event)=>{
    setSurname(event.target.value)
  }

  const nevigate = useNavigate()

  const switchLanguagepage = (event)=>{
    if(name.trim()===""){
      alert("please fill name field")
    }
    else if(surname.trim()===""){
      alert("please fill surname details")
    }
      nevigate("/language")
  }
  return (
    <div className="maininfo">
      <div className="information">
        <label htmlFor="name" >Name:</label>
        <input type="text" value={name} className="inputcontent" placeholder="name" onChange={nameHandler}/>

        <label htmlFor="surname" >Surname:</label>
        <input type="text" value={surname} className="inputcontent" placeholder="surname" onChange={surnameHandler}/>
        <div className="radiocontent">
          <label htmlFor="Gender">Gender:</label>
          <input type="radio" name="Gender" value="Male" />
          Male
          <input type="radio" name="Gender" value="Female" />
          Female
        </div>
      </div>
      <button onClick={switchLanguagepage}>Next</button>
    </div>
  );
};
export default FormData;
