import "./FormData.css";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import { NameShow, SurnameShow } from "./InfoSlice";
import { useDispatch } from "react-redux";

const FormData = () => {
  const dispatch = useDispatch();
  const [name, setName] = useState("");
  const [surname, setSurname] = useState("");

  const nameHandler = (event) => {
    setName(event.target.value);
    // console.log(event.target.value)
    dispatch(NameShow(event.target.value));
  };

  const surnameHandler = (event) => {
    setSurname(event.target.value);
    //console.log(event.target.value)
    dispatch(SurnameShow(event.target.value));
  };

  const nevigate = useNavigate();

  const switchLanguagepage = (event) => {
    if (name.trim() === "") {
      return alert("please fill name field");
    } else if (surname.trim() === "") {
      return alert("please fill surname details");
    }else if ((document.getElementById('male').checked == false ) && (document.getElementById('female').checked == false)){
      return alert("please select gender ")
    }
    nevigate("/language");
  };
  return (
    <div className="maininfo">
      <div className="information">
        <label htmlFor="name">Name:</label>
        <input
          type="text"
          value={name}
          className="inputcontent"
          placeholder="name"
          onChange={nameHandler}
        />

        <label htmlFor="surname">Surname:</label>
        <input
          type="text"
          value={surname}
          className="inputcontent"
          placeholder="surname"
          onChange={surnameHandler}
        />
        <div className="radiocontent">
          <label htmlFor="Gender" >Gender:</label>
          <input id="male" type="radio" name="Gender" value="Male" />
          Male
          <input id="female" type="radio" name="Gender" value="Female" />
          Female
        </div>
      </div>
      <button onClick={switchLanguagepage}>Next</button>
    </div>
  );
};
export default FormData;
