import { useSelector } from "react-redux";
import "./ResultCard.css";
import { quizData, hindidata } from "../Data/QuizData";
import { useState } from "react";
const ResultCard = () => {
  const [visible, setVisible] = useState(false);
  const languageselect = useSelector((state) => state.language.value);
  const language = languageselect ? quizData : hindidata;
  // const score = useSelector((state)=>(state.score.score))
  const answer = useSelector((state) => {
    return state.answer.answer;
  });

  let name = useSelector((state) => {
    return state.information.nameValue;
  });
  let surname = useSelector((state) => {
    return state.information.surnameValue;
  });
  const finalanswer = useSelector((state) => {
    return state.finalanswer.finalanswer;
  });
  let yourans = Object.values(finalanswer);
  console.log(yourans);

  const sc = answer.reduce((acc, que, i) => {
    if (finalanswer[i + 1] === que.answer) acc += 1;
    return acc;
  }, 0);
  console.log({ sc });


  return (
    <div>
      <div className="score-container">
        <p id="paragraph">
          Congratulation! {surname} {name} you are successfully completed your
          exam.
        </p>
        <p id="score">
          Your Score is:
          <span id="scorenumber">
            {sc}/{language.length}
          </span>
        </p>
      </div>
      <button
        id="view"
        onClick={() => {
          setVisible(!visible);
        }}
      >
        View Answer
      </button>

      {/* <div className="result">
        <div>
          <p>Your Answer:</p>
          {yourans.map((ans) => (
            <div>
              <span id="key">{ans[0]}:</span>
              <span>{ans[1]}</span>
            </div>
          ))}
        </div>

        <div>
          <p>Correct Answer:</p>
          {answer.map((correct) => (
            <div>{correct.answer}</div>
          ))}
        </div>
      </div> */}

      {visible && (
        <div className="maincontent">
          {language.map((item, i) => {
            return (
              <div>
                <section className="mainsection">
                  <div>
                    <span>{i + 1}</span>
                    <span>{item.question}</span>
                  </div>
                  <div className="showoption">
                    {item.options.map((option,i) => {
                      return (
                        <button
                          className={
                            function report(i) {
                              if (item[i]=== yourans[i]) {
                                if(item[i].answer === yourans[i] ){
                                  return true
                                }

                              } else {
                                return false;
                              }
                            }
                              ? "green"
                              : "red"
                          }
                        >
                          {option}
                        </button>
                      );
                    })}
                  </div>
                  <div>
                    <p id="correct">CORRECT ANSWER: {item.answer}</p>
                    <p id="ownans">YOUR ANSWER: {yourans[i]}</p>
                  </div>
                </section>
              </div>
            );
          })}
        </div>
      )}
    </div>
  );
};
export default ResultCard;
