import { configureStore } from '@reduxjs/toolkit'
import {LanguageSlice} from '../Component/LanguageSlice';
import { AnswerSlice } from '../Component/AnswerSlice';
import { InfoSlice } from '../Component/InfoSlice';
import { FinalAnswerSlice } from '../Component/FinalAnswerSlice';
import { ScoreSlice,  } from '../Component/ScoreSlice';

const store = configureStore({
  reducer: {
    language:LanguageSlice.reducer,
    answer:AnswerSlice.reducer,
    information:InfoSlice.reducer,
    finalanswer: FinalAnswerSlice.reducer,
    score: ScoreSlice.reducer


  },
})

export default store;
